INSERT INTO public.films_attrs_values (id, film_id, attr_id, value_float, value_date, value_text, value_bool, value_int) VALUES (1, 1, 1, null, null, 'Удивился ли я, посмотрев один из самых качественных боевиков за последнее время? Конечно! Изумился ли я, когда увидел в графе ''режиссер'' Илью Найшуллера? Разумеется! ', null, null);
INSERT INTO public.films_attrs_values (id, film_id, attr_id, value_float, value_date, value_text, value_bool, value_int) VALUES (2, 1, 2, null, null, 'Я не собираюсь говорить, что «Никто» плохой фильм. Нет, хороший и Найшуллер молодец. Удачи ему! Фильм можно смело глянуть и получить неописуемое удовольствие.', null, null);
INSERT INTO public.films_attrs_values (id, film_id, attr_id, value_float, value_date, value_text, value_bool, value_int) VALUES (3, 2, 1, null, null, 'Фильму сильно не хватает того напряжения, которое свойственно лучшим представителям шпионского жанра. Конечно, как драма о настоящей дружбе и человеческих отношениях он смотрится более чем не плохо, но принимая во внимание потенциал реальной истории и обстоятельства, в которых она происходила, не покидает ощущение упущенных возможностей. Мог получиться гнетущий шпионский триллер, а на выходе - лишь приятная драма на пару просмотров с весьма спорной точкой зрения авторов, открыто романтизирующих предателя Родины.', null, null);
INSERT INTO public.films_attrs_values (id, film_id, attr_id, value_float, value_date, value_text, value_bool, value_int) VALUES (4, 2, 2, null, null, 'Такие фильмы нужно смотреть очень внимательно, и вы точно получите удовольствие от насыщенной событиями, непредсказуемой шпионской игры.', null, null);
INSERT INTO public.films_attrs_values (id, film_id, attr_id, value_float, value_date, value_text, value_bool, value_int) VALUES (5, 3, 1, null, null, 'Сам по себе фильм и внимание способен привлечь (все таки тема наркотиков, их перевозки и распространения — одна из наиболее существенных в нашем сегодняшнем мире), и актерским составом заинтересовать, однако глубины и проработанности ему все же не хватает. ', null, null);
INSERT INTO public.films_attrs_values (id, film_id, attr_id, value_float, value_date, value_text, value_bool, value_int) VALUES (6, 3, 2, null, null, 'Это умное и холодное кино, заполненное терминами и сложной болтовней, но при этом ни разу не вызывающее уныния. Во многом это благодаря замечательным артистам, собравшимся в таком проекте не для всех. ', null, null);
